import React, { useState } from 'react';
import { ClientDetails } from '../types';

type ClientFormProps = {
  onChange: (details: ClientDetails) => void;
};

const initialState: ClientDetails = {
  business_name: '',
  contact_name: '',
  first_name: '',
  business_email: '',
  telephone: '',
  notes: '',
  business_type: ''
};

const ClientForm: React.FC<ClientFormProps> = ({ onChange }) => {
  const [form, setForm] = useState(initialState);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    const updated = { ...form, [name]: value };
    setForm(updated);
    onChange(updated);
  };

  return (
    <form>
      <input name="business_name" placeholder="Business Name" value={form.business_name} onChange={handleChange} />
      <input name="contact_name" placeholder="Contact Name" value={form.contact_name} onChange={handleChange} />
      <input name="first_name" placeholder="First Name" value={form.first_name} onChange={handleChange} />
      <input name="business_email" placeholder="Business Email" value={form.business_email} onChange={handleChange} />
      <input name="telephone" placeholder="Telephone" value={form.telephone} onChange={handleChange} />
      <input name="business_type" placeholder="Business Type" value={form.business_type} onChange={handleChange} />
      <textarea name="notes" placeholder="Notes" value={form.notes} onChange={handleChange} />
    </form>
  );
};

export default ClientForm;
