import React, { useState } from 'react';
import ClientForm from './components/ClientForm';
import { emailTemplates } from './emailTemplates';
import { ClientDetails } from './types';
import emailjs from 'emailjs-com';
import { supabase } from './supabaseClient';

const App: React.FC = () => {
  const [clientDetails, setClientDetails] = useState<ClientDetails | null>(null);
  const [selectedTemplateId, setSelectedTemplateId] = useState<string>('');
  const [step, setStep] = useState<'entry' | 'preview' | 'sent'>('entry');
  const [sending, setSending] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const selectedTemplate = emailTemplates.find(t => t.id === selectedTemplateId);

  // Fill template with client info
  const fillTemplate = (template: string) => {
    if (!clientDetails) return template;
    return template
      .replace('{{name}}', clientDetails.first_name)
      .replace('{{business}}', clientDetails.business_name);
  };

  const handleClientDetailsChange = (details: ClientDetails) => setClientDetails(details);
  const handleEmailTemplateChange = (id: string) => setSelectedTemplateId(id);

  const handleSend = async () => {
    if (!clientDetails || !selectedTemplate) return;
    setSending(true);
    setError(null);
    try {
      // 1. Send email via EmailJS
      await emailjs.send(
        "service_02yclcc",
        selectedTemplate.id,
        {
          to_name: clientDetails.first_name,
          to_email: clientDetails.business_email,
          message: fillTemplate(selectedTemplate.body),
          subject: selectedTemplate.subject,
        },
        "YOUR_EMAILJS_PUBLIC_KEY"
      );
      // 2. Store record in Supabase
      const { error } = await supabase.from('clientcontact').insert([clientDetails]);
      if (error) throw error;
      setStep('sent');
    } catch (err: any) {
      console.error('Send error:', err);
      setError(err.message || JSON.stringify(err) || 'Failed to send email or save record.');
    } finally {
      setSending(false);
    }
  };

  return (
    <div>
      {step === 'entry' && (
        <ClientForm
          onChange={handleClientDetailsChange}
          onNext={() => setStep('preview')}
          emailTemplates={emailTemplates}
          selectedTemplateId={selectedTemplateId}
          onTemplateChange={handleEmailTemplateChange}
        />
      )}
      {step === 'preview' && clientDetails && selectedTemplate && (
        <div style={{
          background: '#c3d82e',
          borderRadius: '16px',
          padding: '2em',
          width: 350,
          margin: '2em auto',
          fontFamily: 'sans-serif',
          boxSizing: 'border-box'
        }}>
          <h2>Email Preview</h2>
          <strong>To:</strong> {clientDetails.business_email}<br />
          <strong>Subject:</strong> {selectedTemplate.subject}<br />
          <strong>Body:</strong>
          <pre style={{ background: '#fff', padding: '1em', borderRadius: 4 }}>{fillTemplate(selectedTemplate.body)}</pre>
          {error && <div style={{ color: 'red', marginBottom: 8 }}>{error}</div>}
          <button
            onClick={handleSend}
            disabled={sending}
            style={{
              width: '100%',
              padding: '0.5em',
              background: '#fff',
              color: '#333',
              border: 'none',
              borderRadius: 3,
              fontWeight: 600,
              fontSize: 16,
              cursor: 'pointer'
            }}
          >
            {sending ? 'Sending...' : 'Send'}
          </button>
        </div>
      )}
      {step === 'sent' && (
        <div style={{
          background: '#c3d82e',
          borderRadius: '16px',
          padding: '2em',
          width: 350,
          margin: '2em auto',
          fontFamily: 'sans-serif',
          boxSizing: 'border-box',
          textAlign: 'center'
        }}>
          <h2>Success!</h2>
          <p>The email was sent and the record was saved.</p>
          <button
            onClick={() => {
              setClientDetails(null);
              setSelectedTemplateId('');
              setStep('entry');
            }}
            style={{
              marginTop: 12,
              width: '100%',
              padding: '0.5em',
              background: '#fff',
              color: '#333',
              border: 'none',
              borderRadius: 3,
              fontWeight: 600,
              fontSize: 16,
              cursor: 'pointer'
            }}
          >
            Add Another
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
