export const emailTemplates = [
  {
    id: 'welcome',
    subject: 'Introducing 360째 Virtual Tour Services for {business_name}',
    body: `Hello {first_name},

I hope this message finds you well. My name is Karl, a Belfast-based virtual tour photographer and Google partner. I'm contacting you to explore the possibility of creating a high-quality Google 360째 virtual tour for {business_name}.

I will be in the {business_location} area on {return_visit} and would like to schedule a brief meeting with you or the owner to discuss a proposal and answer any questions they may have.

Best regards,
Karl`
  },
  // Add more templates here
];

export const fillTemplate = (template: string, details: any) => {
  return template
    .replace(/{first_name}/g, details.first_name)
    .replace(/{business_name}/g, details.business_name)
    .replace(/{business_location}/g, details.business_location)
    .replace(/{return_visit}/g, details.return_date);
};
